package task1_practical;

public class foorloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Number\tSquare");

        // For loop from 1 to 10
        for (int i = 1; i <= 10; i++) {
            int square = i * i;
            System.out.println(i + "\t" + square);

	}

}
}
